CREATE TABLE ticket (id BIGINT AUTO_INCREMENT, bubble_tag VARCHAR(13), amount FLOAT(18, 2), status BIGINT, expire_at DATE, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, UNIQUE INDEX bubble_tag_index_idx (bubble_tag), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE ticket_tracking (id BIGINT AUTO_INCREMENT, operator_id BIGINT NOT NULL, actor_id BIGINT NOT NULL, is_remove_action BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE ticket_tracking_entry (id BIGINT AUTO_INCREMENT, status BIGINT NOT NULL, ticket_id BIGINT NOT NULL, ticket_tracking_id BIGINT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX created_at_index_idx (created_at), INDEX ticket_tracking_id_idx (ticket_tracking_id), INDEX ticket_id_idx (ticket_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
ALTER TABLE ticket_tracking_entry ADD CONSTRAINT ticket_tracking_entry_ticket_tracking_id_ticket_tracking_id FOREIGN KEY (ticket_tracking_id) REFERENCES ticket_tracking(id) ON DELETE CASCADE;
ALTER TABLE ticket_tracking_entry ADD CONSTRAINT ticket_tracking_entry_ticket_id_ticket_id FOREIGN KEY (ticket_id) REFERENCES ticket(id) ON DELETE CASCADE;
